const loopItem = $('逐条处理').item;
const serpRes = $input.first().json;
const trendingId = loopItem.json.trending_id;
const ipId = loopItem.json.ip_id;
const searchKeyword = loopItem.json.search_keyword;

// 检查 API 响应
if (serpRes.error || !serpRes.interest_over_time) {
  return [{
    json: {
      error: true,
      trendingId,
      ipId,
      message: serpRes.error || '无趋势数据'
    }
  }];
}

// 提取时间序列数据
const timelineData = serpRes.interest_over_time?.timeline_data || [];

if (timelineData.length === 0) {
  return [{
    json: {
      error: true,
      trendingId,
      ipId,
      message: '时间线数据为空'
    }
  }];
}

// 获取最新的热度值（最后一个数据点）
const latestPoint = timelineData[timelineData.length - 1];
const latestValue = latestPoint.values?.[0]?.extracted_value || 0;

// 归一化热度值 (Google Trends 返回 0-100)
const normalizedValue = latestValue;

// 计算 7 天前的值用于变化率计算
const sevenDaysAgoIndex = Math.max(0, timelineData.length - 8);
const oldPoint = timelineData[sevenDaysAgoIndex];
const oldValue = oldPoint?.values?.[0]?.extracted_value || 0;

// 计算 7 日变化率
let changeRate = null;
if (oldValue > 0) {
  changeRate = Math.round(((latestValue - oldValue) / oldValue * 100) * 100) / 100;
}

return [{
  json: {
    trendingId,
    ipId,
    searchKeyword,
    googleTrend: latestValue,
    normalizedValue,
    oldValue,
    googleTrendChange: changeRate,
    metadata: {
      geo: 'US',
      date: 'today 1-m',
      dataPoints: timelineData.length
    }
  }
}];

return [{
  json: {
    success: true,
    message: 'Google Trends 数据更新完成',
    source: 'GOOGLE_TRENDS',
    timestamp: new Date().toISOString()
  }
}];
