// 解析请求数据
const body = $input.first().json.body;
const { taskId, title, videoUrl, coverUrl, seriesTitle, platforms, platformContents } = body;

if (!taskId || !platforms?.length) {
  throw new Error('Missing required fields: taskId or platforms');
}

// 为每个平台生成发布任务
const publishJobs = platforms.map(platform => {
  const content = platformContents?.find(c => c.platform === platform) || {};
  return {
    json: {
      taskId,
      platform,
      title: content.title || title,
      description: content.description || '',
      hashtags: content.hashtags || [],
      videoUrl,
      coverUrl,
      seriesTitle,
    }
  };
});

return publishJobs;

// 模拟发布结果
const item = $input.first().json;
const success = Math.random() > 0.1; // 90% 成功率

const platformUrls = {
  INSTAGRAM: 'https://instagram.com/p/',
  THREADS: 'https://threads.net/@moedesk/post/',
  YOUTUBE: 'https://youtube.com/shorts/',
};

const result = {
  taskId: item.taskId,
  platform: item.platform,
  success,
  externalId: success ? `${item.platform.toLowerCase()}_${Date.now()}_${Math.random().toString(36).slice(2, 8)}` : null,
  externalUrl: success ? `${platformUrls[item.platform] || 'https://example.com/'}${Date.now()}` : null,
  errorMessage: success ? null : 'API rate limit exceeded (simulated error)',
  publishedAt: success ? new Date().toISOString() : null,
};

return [{ json: result }];

// 汇总所有发布结果
const items = $input.all();
const results = items.map(item => item.json);

const successCount = results.filter(r => r.success).length;
const failedCount = results.filter(r => !r.success).length;

return [{
  json: {
    success: true,
    message: '发布任务已处理',
    summary: {
      total: results.length,
      success: successCount,
      failed: failedCount,
    },
    results,
  }
}];
